%% Above thresh normalization for all slices with kept ROI
% Script to get only activity above threshold and normalize to each cell
% to it's own max

function [ShamAboveThresh, TBIAboveThresh] = ThresholdNormalization(ShamData,TBIData,ShamKeptROI,TBIKeptROI)

for i = 1:length(ShamData) % iterate across data
    for c = 1:length(ShamData{i,1}) % iterate across each session
        % Set new cell = to tSeriesF data of kept ROI
        if isempty(ShamData{i,1}{c,1})
            ShamAboveThresh{i,1}{c,1} = [];
            continue
        else
        ShamAboveThresh{i,1}{c,1} = ShamData{i,1}{c,1}.Ca.tSeriesF(:,ShamKeptROI{i,1}{c,1});
        % Set new cell = to the threshold for kept ROI to call later
        ShamKeptThresh{i,1}{c,1} = ShamData{i,1}{c,1}.Ca.baseThresh(ShamKeptROI{i,1}{c,1});
        % iterate across each cell
        for d = 1:length(ShamKeptROI{i,1}{c,1})
            for e = 1:length(ShamAboveThresh{i,1}{c,1}(:,d)) % iterate across time
                % Add logic to set points less than thresh = 0
                if ShamAboveThresh{i,1}{c,1}(e,d) < ShamKeptThresh{i,1}{c,1}(d)
                    ShamAboveThresh{i,1}{c,1}(e,d) = 0;
                end
            end
            % Find the max for that slice
            ShamMax(d) = max(ShamAboveThresh{i,1}{c,1}(:,d));
            % Divide (normalize) tSeries by the max
            ShamAboveThresh{i,1}{c,1}(:,d) = ShamAboveThresh{i,1}{c,1}(:,d)./ShamMax(d);
        end
    end
end
end

%%
% Repeat for TBI
for i = 1:length(TBIData) % iterate across data
    for c = 1:length(TBIData{i,1}) % iterate across each session
        % Set new cell = to tSeriesF data of kept ROI
        
        if isempty(TBIData{i,1}{c,1})
            TBIAboveThresh{i,1}{c,1} = [];
            
            continue
        else
        
        TBIAboveThresh{i,1}{c,1} = TBIData{i,1}{c,1}.Ca.tSeriesF(:,TBIKeptROI{i,1}{c,1});
        % Set new cell = to the threshold for kept ROI to call later
        TBIKeptThresh{i,1}{c,1} = TBIData{i,1}{c,1}.Ca.baseThresh(TBIKeptROI{i,1}{c,1});
        % iterate across each cell
        for d = 1:length(TBIKeptROI{i,1}{c,1})
            for e = 1:length(TBIAboveThresh{i,1}{c,1}(:,d)) % iterate across time
                % Add logic to set points less than thresh = 0
                if TBIAboveThresh{i,1}{c,1}(e,d) < TBIKeptThresh{i,1}{c,1}(d)
                    TBIAboveThresh{i,1}{c,1}(e,d) = 0;
                end
            end
            % Find the max for that slice
            TBIMax(d) = max(TBIAboveThresh{i,1}{c,1}(:,d));
            % Divide (normalize) tSeries by the max
            TBIAboveThresh{i,1}{c,1}(:,d) = TBIAboveThresh{i,1}{c,1}(:,d)./TBIMax(d);
        end
    end
end
end
